WHERE board_num=#{boardNum}

mapper.select:
	SELECT * FROM qna



 	ORDER BY board_ref DESC, board_step ASC
 	LIMIT #{startNum} , #{perPage}

mapper.update:
UPDATE qna
SET board_ref=#{boardNum}
WHERE board_num=#{boardNum}

UPDATE qna SET board_step = board_step + 1
WHERE board_ref = #{boardRef}
AND board_step > #{boardStep}
